DROP TABLE IF EXISTS `myhome_rec`;

CREATE TABLE `myhome_rec` (
  `id` bigint NOT NULL AUTO_INCREMENT,
  `score` decimal(5,2) NOT NULL,
  `food_id` bigint NOT NULL,
  `user_id` bigint NOT NULL,
  PRIMARY KEY (`id`),
  KEY `myhome_rec_food_id_6148ee24_fk_myhome_foods_id` (`food_id`),
  KEY `myhome_rec_user_id_cd737d97_fk_users_id` (`user_id`),
